from collections import Counter, defaultdict
from math import factorial
from typing import List


class Solution:
    def threeSumMulti(self, arr: List[int], target: int) -> int:
        def ways_to_pick(r, n):
            '''
            how many ways can you pick r items out of n items if order does not matter
            i.e. how many times can you pick 2 twos out of [2 2 2 2 2]
            '''
            if r > n:
                return 0
            return factorial(n) / (factorial(r) * factorial(n - r))

        available = Counter(arr)
        total_combos = 0

        for num1 in available:
            for num2 in available:
                if num2 >= num1:
                    num3 = target - num1 - num2
                    if num3 >= num2 and num3 in available:
                        required = defaultdict(int)
                        required[num1] += 1
                        required[num2] += 1
                        required[num3] += 1

                        ways = 1
                        for number in required:
                            ways *= ways_to_pick(required[number], available[number])

                        total_combos += ways

        return int(total_combos) % (10 ** 9 + 7)


solution = Solution()
